    //         //          <TouchableOpacity onPress={() => pressHandler(item.id)}>
    //         //             <View>
    //         //                 <View  style={{flexDirection:'row',alignContent:"space-between"}}>
    //         //                     <Image 
    //         //                         style={styles.image}
    //         //                         source={{uri:item.imageUri}}
    //         //                         />
    //         //                     <View style={{padding:"4px"}}>
    //         //                         <Text style={{fontWeight:'bold'}}>{item.name}</Text>
    //         //                         <Text style={{fontWeight:'bold'}}>Rs. {item.cost}</Text>
    //         //                         <Text style={{fontWeight:'bold'}}>{item.currentAge} old</Text>
    //         //                     </View>
    //         //                 </View>
    //         //                 <View style={{borderBottomColor:"#000",borderBottomWidth:"2px"}}></View>
    //         //             </View>
    //         //        </TouchableOpacity>
    //         //       )}
    //         //    /> 

      
    //       </ScrollView>
    //       //</View> 
    // );
}


const styles= StyleSheet.create({
    container:{
        padding:"10px",
        flex:1,
    },

    image:{
        borderRadius:"10px",
        width:imageWidth,
        height:imageHeight,
        margin:"2px",
    },
    shadow:{
        shadowColor: '#000000',
        shadowOffset: {
            width: 0,
            height: 3
        },
        shadowRadius: "10px",
        shadowOpacity: 1.0,
    },
    name:{
        top:-30,
        backgroundColor:"#fff",
        borderRadius:"30px",
        height:'40px',
        width:"250px",
        margin:"3px",
        justifyContent:"center",
        alignContent:"center",
    },
    nameTag:{
        alignSelf:"center",
        fontWeight:"bold",
        fontSize:20,
    }


});
