import React, { useState } from 'react';
import { StyleSheet, Text,Image, View,ScrollView, TextInput, FlatList, TouchableOpacity,Dimensions  } from 'react-native';

import firebase from 'firebase';
import Icon from 'react-native-vector-icons/MaterialCommunityIcons';

const { width: screenWidth} = Dimensions.get('window')

const imageWidth = screenWidth*.20;
const imageHeight = imageWidth*.8;


export default function PetDetailsScreen() {
    // const { itemId, otherParam } = route.params;
    const CartScreen = firebase.database().ref("Cart");
 
    const [data,setData] = React.useState("Loading")
    
    React.useEffect(() => {
       CartScreen.on("value",dataSnap => {
        setData(dataSnap);
       });
    },[]);

    // const handleCartPress = () => {
    //     const myCart = firebase.database().ref("Cart"); 
    //     myCart.push(data);
    // }

    // const handleBuyPress = () => {
    //     console.log(data.name," is added to cart");
    // }

    return(
        <View style={styles.container}>

          {
          data.map((cartItem,index) => (
            <View>
              <View key={index}>
                <Image 
                  source={{uri:cartItem.imageUri}}
                  />
                <Text style={{fontWeight:'bold'}}>{cartItem.name}</Text>
              </View>
            <View style={{borderBottomColor:lightgreencolor,borderBottomWidth:"2px"}}></View>
            </View>
          ))}


        </View>
    );
}


const styles= StyleSheet.create({
    container:{
        flex:1,
        padding:"10px",
        justifyContent:"center",
        alignItems:"center",
        backgroundColor:"#ccc",
    },

    image:{
        borderRadius:"10px",
        width:imageWidth,
        height:imageHeight,
    },
    shadow:{
        shadowColor: '#000000',
        shadowOffset: {
            width: 0,
            height: 3
        },
        shadowRadius: "10px",
        shadowOpacity: 1.0,
    },
    name:{
        top:-30,
        backgroundColor:"#fff",
        borderRadius:"30px",
        height:'40px',
        width:"250px",
        margin:"3px",
        justifyContent:"center",
        alignContent:"center",
    },
    nameTag:{
        alignSelf:"center",
        fontWeight:"bold",
        fontSize:20,
    }


});
