import React, { useState } from 'react';
import { StyleSheet, Text,Image, View,ScrollView, TextInput, FlatList, TouchableOpacity,Dimensions  } from 'react-native';

import firebase from 'firebase';
import Icon from 'react-native-vector-icons/MaterialCommunityIcons';

const { width: screenWidth} = Dimensions.get('window')

const imageWidth = screenWidth-20;
const imageHeight = imageWidth*.8;


export default function PetDetailsScreen() {
    // const { itemId, otherParam } = route.params;
    const DetailsScreen = firebase.database().ref("DetailsScreen");
    const [details,setDetails] = React.useState({
        id:"Loading",
        type:"Loading"
    });

    const [data,setData] = React.useState("Loading")
    
    React.useEffect(() => {
        DetailsScreen.on("value",detailsSnap => {
            setDetails(detailsSnap.val());
            const Data = firebase.database().ref(detailsSnap.val().type);
            Data.on("value",data => {
                var dataVal = Object.values(data.val()).filter(function (element){
                    return element.id === detailsSnap.val().id;
                });                
                setData(dataVal[0]);
            })
        })  
    },[]);

    const handleCartPress = () => {
        const myCart = firebase.database().ref("Cart"); 
        CartScreen.push(data);
    }

    const handleBuyPress = () => {
        console.log(data.name," is added to cart");
    }

    return(
        <View style={styles.container}>
            <View style={[{borderRadius:"10px"},styles.shadow]}>
                <Image 
                    style = {styles.image}
                    source={{uri:data.imageUri}}
                />
            </View>
            <View style={[styles.name,styles.shadow]}>
                <Text style={styles.nameTag}>{data.name}</Text>
            </View>

            <View style={[{alignSelf:"flex-start",width:"100%",padding:"10px"},styles.shadow]}>
                <Text><Text style={{fontWeight:"bold",fontSize:17}}>Cost:           </Text>{data.cost}</Text>
                <Text><Text style={{fontWeight:"bold",fontSize:17}}>Life Span:      </Text>{data.lifeSpan}</Text>
                <Text><Text style={{fontWeight:"bold",fontSize:17}}>Current Age:    </Text>{data.currentAge}</Text>
                <Text><Text style={{fontWeight:"bold",fontSize:17}}>Location:      </Text>{data.location}</Text>
                <Text><Text style={{fontWeight:"bold",fontSize:17}}>Disabled:      </Text>{data.handicap}</Text>
            </View>

            <TouchableOpacity onPress = {() => handleCartPress()}>
                <View 
                    style={[styles.name,styles.shadow,{top:10,flexDirection:"row",paddingTop:"9px"}]}
                    onPress = {() => handleCartPress()}
                    >
                    <Icon
                        size={20}
                        name="cart-outline"  
                        />
                        <Text style={{fontWeight:"bold",fontSize:17}}>Add to cart</Text>
                </View>
            </TouchableOpacity>            

            <TouchableOpacity onPress = {() => handleBuyPress()}>
                <View style={[styles.name,styles.shadow,{top:10,flexDirection:"row",paddingTop:"9px"}]}>
                    <Icon
                        size={20}
                        name="cart-outline"  
                        />
                    <Text style={{fontWeight:"bold",fontSize:17}}>Buy</Text>
            </View>
            </TouchableOpacity>            

        </View>
    );
}


const styles= StyleSheet.create({
    container:{
        flex:1,
        padding:"10px",
        justifyContent:"center",
        alignItems:"center",
        backgroundColor:"#ccc",
    },

    image:{
        borderRadius:"10px",
        width:imageWidth,
        height:imageHeight,
    },
    shadow:{
        shadowColor: '#000000',
        shadowOffset: {
            width: 0,
            height: 3
        },
        shadowRadius: "10px",
        shadowOpacity: 1.0,
    },
    name:{
        top:-30,
        backgroundColor:"#fff",
        borderRadius:"30px",
        height:'40px',
        width:"250px",
        margin:"3px",
        justifyContent:"center",
        alignContent:"center",
    },
    nameTag:{
        alignSelf:"center",
        fontWeight:"bold",
        fontSize:20,
    }


});
