    const screen = ({ isFocused }) => !isFocused ? null : (
      <ScrollView >
      
        <View style={styles.container}>

          <View style={styles.header}>
              <View style={styles.inputBox}>
                <View style={styles.action}>
                    <FontAwesome
                      style={{color:'#333'}}
                      size={ 20 }
                      name="search"
                      color="#05375a"
                    />
                    <TextInput 
                      placeholder="Search"
                      style={styles.textInput}
                      autoCapitalize="none"
                      onChangeText = {(val) => textInputChange(val)}
                    />
                </View>
              </View>
          </View>
        
          <View style={styles.headerAndBody}>
            {
              header.map((data,index) => (
                <View key={index} style={styles.headerAndBodyItem}>
                  <Image 
                    style = {styles.HeaderAndBodyImage}
                    source={{uri:data.uri}}
                    />
                  <Text style={{fontWeight:'bold'}}>{data.name}</Text>
                </View>
              ))
            }
          </View>
        
          <View style={styles.body}>
            {
              categories.map((category,index) => (
                <View key={index}>
                      <View style={styles.FeatureHeader}>
                        <Text style={{fontWeight:'bold'}}>{category.name}</Text>
                      </View> 
                          <FlatList 
                            horizontal
                            keyExtractor={(item, index) => index.toString()}
                            showsHorizontalScrollIndicator={false}
                            data = {category.data}
                            renderItem={({ item }) => (
                              <TouchableOpacity onPress={() => pressHandler(item.id)}>
                                <Image 
                                  style = {styles.FeatureImage}
                                  source={{uri:item}}
                                />
                              </TouchableOpacity>
                            )}
                          />
                </View>

              ))
            }
          </View> 
        </View> 
            
      </ScrollView>
 
 );
          
 export default withNavigation(screen);

