                source={{uri:'https://images.ctfassets.net/hrltx12pl8hq/3MbF54EhWUhsXunc5Keueb/60774fbbff86e6bf6776f1e17a8016b4/04-nature_721703848.jpg?fit=fill&w=480&h=270'}}
              />
              <Text style={{fontWeight:'bold'}}>Dogs</Text>
            </View><View style={styles.headerAndBodyItem}>
              <Image 
                style = {styles.HeaderAndBodyImage}
                source={{uri:'https://images.ctfassets.net/hrltx12pl8hq/3MbF54EhWUhsXunc5Keueb/60774fbbff86e6bf6776f1e17a8016b4/04-nature_721703848.jpg?fit=fill&w=480&h=270'}}
              />
              <Text style={{fontWeight:'bold'}}>Dogs</Text>
            </View><View style={styles.headerAndBodyItem}>
              <Image 
                style = {styles.HeaderAndBodyImage}
                source={{uri:'https://images.ctfassets.net/hrltx12pl8hq/3MbF54EhWUhsXunc5Keueb/60774fbbff86e6bf6776f1e17a8016b4/04-nature_721703848.jpg?fit=fill&w=480&h=270'}}
              />
              <Text style={{fontWeight:'bold'}}>Dogs</Text>
            </View>

          </View>
        



            <View style={styles.body}>
              {
                categories.map((category,index) => (
                  <View key={index}>
                    <View style={styles.FeatureHeader}>
                      <Text style={{fontWeight:'bold'}}>{category.name}</Text>
                    </View>

                    <FlatList 
                    horizontal
                    keyExtractor={(item, index) => index.toString()}
                    showsHorizontalScrollIndicator={false}
                    data = {category.data}
                    renderItem={({ item }) => (
                      <TouchableOpacity onPress={() => pressHandler(item.id)}>
                        <Image 
                          style = {styles.FeatureImage}
                          source={{uri:item}}
                        />
                      </TouchableOpacity>
                    )}
                  />
                </View>
                ))
              } 
          </View>
        
        </View>
      </ScrollView>
     
  );
}

