import * as React from 'react';
import { Text, View, TouchableOpacity, Dimensions,StyleSheet, StatusBar } from 'react-native';

import * as Animatable from 'react-native-animatable';
// import MaterialIcons from "react-native-vector-icons/MaterialCommunityIcons";

export default function AdoptScreen({navigation}) {
    return (
      <View style={styles.container}>

        <StatusBar backgroundColor='#009387' barStyle="light-content"/>
        
        <View style={styles.header}>
          <Animatable.Image
            animation="bounceIn"
            duration = {2000}
            source={require("../assets/icon.png")}
            style={styles.logo}
            resizeMode="stretch"
          />
        </View>
 
        <Animatable.View 
          animation="fadeInUpBig"
          style={styles.footer}>
          <Text style={styles.title}>Splash Screen!!!</Text>
          <Text style={styles.text}>Splash Screen!!!</Text>
          <View style={styles.button}>
            <TouchableOpacity onPress={()=>{navigation.navigate('SignInScreen')}}>
              <Text style={styles.textSign}>Getting Started
              <Text style={{fontSize:20,color:"#fff"}}>></Text></Text>
            </TouchableOpacity>
          </View>
        </Animatable.View>

      </View>
    );
} 


const {height} = Dimensions.get("screen");
const height_logo = height * 0.28;

const styles = StyleSheet.create({
  container: {
    flex: 1, 
    backgroundColor: '#009387'
  },
  header: {
      flex: 2,
      justifyContent: 'center',
      alignItems: 'center'
  },
  footer: {
      flex: 1,
      backgroundColor: '#fff',
      borderTopLeftRadius: 30,
      borderTopRightRadius: 30,
      paddingVertical: 50,
      paddingHorizontal: 30
  },
  logo: {
      width: height_logo,
      height: height_logo,
      borderRadius:height_logo*2,
      backgroundColor: '#009387'

  },
  title: {
      color: '#05375a',
      fontSize: 30,
      fontWeight: 'bold'
  },
  text: {
      color: 'grey',
      marginTop:5
  },
  button: {
      alignItems: 'flex-end',
      marginTop: 30
  },
  signIn: {
      width: 150,
      height: 40,
      justifyContent: 'center',
      alignItems: 'center',
      borderRadius: 50,
      flexDirection: 'row'
  },
  textSign: {
      color: 'white',
      fontWeight: 'bold',
      padding:8,
      borderRadius:50,
      width:150,
      textAlign:"center",
      backgroundColor:"#08d4c4",
  }
})
