  const { signOut } = React.useContext( AuthContext );
  const signOutHandle = () => {
    signOut();  
  }
  
  const myHeader = firebase.database().ref("items");




  React.useEffect(() => {
      //Handle SignOut after pressing Signout Button
      firebase.auth().onAuthStateChanged((user) => {
        if (!user) {
          signOutHandle();
        } else {

          myHeader.push().set({
            name:"tejusVarma"
          })

        } 
      });


  });
  
