import React, { useState } from 'react';
import { StyleSheet, Text,Image, View,ScrollView, TextInput, FlatList, TouchableOpacity,Dimensions  } from 'react-native';

import firebase from 'firebase';


const { width: screenWidth} = Dimensions.get('window')

const imageWidth = screenWidth-20;
const imageHeight = imageWidth*.8;


export default function PetDetailsScreen() {
    // const { itemId, otherParam } = route.params;
    const DetailsScreen = firebase.database().ref("DetailsScreen");
    const [details,setDetails] = React.useState({
        id:"Loading",
        type:"Loading"
    });

    const [data,setData] = React.useState("Loading")
    
    React.useEffect(() => {
        DetailsScreen.on("value",detailsSnap => {
            setDetails(detailsSnap.val());
            const Data = firebase.database().ref(detailsSnap.val().type);
            Data.on("value",data => {
                var dataVal = Object.values(data.val()).filter(function (element){
                    return element.id === detailsSnap.val().id;
                });                
                setData(dataVal[0]);
            })
        })  
    },[]);

    return(
        <View style={styles.container}>
            <Image 
                style = {styles.image}
                source={{uri:data.imageUri}}
            />
            <View style={styles.name}>
                <View style={{alignSelf:"flex-start"}}>
                    <Image 
                        style = {styles.tagImage}
                        source={{uri:data.imageUri}}
                        />
                </View>
                <View style={{alignSelf:"center"}}>
                    <Text style={styles.nameTag}>{data.name}</Text>

                </View>
            </View>
        </View>
    );
}


const styles= StyleSheet.create({
    container:{
        padding:"10px",
        justifyContent:"center",
        alignItems:"center",
    },

    image:{
        width:imageWidth,
        height:imageHeight,
        borderRadius:"10px",
    },
    name:{
        flexDirection:"row",
        top:-30,
        backgroundColor:"#fff",
        borderRadius:"30px",
        height:'40px',
        width:"250px",
        // justifyContent:"center",
    },
    tagImage:{
        flex:1,
        padding:"3px",
        height:"34px",
        width:"34px",
        borderRadius:"50%",
    },
    nameTag:{
        flex:1,
        alignSelf:"center",
        fontVariant:"bold",
    }


});
