import React, { useState } from 'react';
import { StyleSheet, Text,Image, View,ScrollView, TextInput, FlatList, TouchableOpacity,Dimensions  } from 'react-native';

import firebase from "firebase";

export default function App() {
  const [user,setUser] = useState({
    name:"",
    email:"",
    phoneNumber:"",
    photoUrl:"",
  });

  React.useEffect(() => {
    const userFB = firebase.auth().currentUser;
    if(userFB != null){
      setUser({
        name:userFB.name,
        email:userFB.email,
        phoneNumber:userFB.phoneNumber,
        photoUrl:userFB.photoUrl
      })
    }
 },[]);


  return (
     <ScrollView>
       <View style={styles.container}>
          <View style={styles.section}>
            
            <View style={styles.sectionItemLeft}>
              <Text style={styles.text}>Name</Text>
            </View>

            <View style={styles.sectionItemRight}>
                <TextInput 
                  style={styles.input}
                  // onChangeText={onChangeNumber}
                  value={user.name}
                  placeholder="useless placeholder"
                  keyboardType="numeric"
                  />
            </View>
          
          </View>
          <View style={styles.section}>
            
            <View style={styles.sectionItemLeft}>
              <Text style={styles.text}>E-Mail</Text>
            </View>

            <View style={styles.sectionItemRight}>
                <TextInput 
                  style={styles.input}
                  // onChangeText={onChangeNumber}
                  value={user.email}
                  placeholder="useless placeholder"
                  keyboardType="numeric"
                  />
            </View>
          
          </View>
       </View>
     </ScrollView>
  );
}

const styles = StyleSheet.create({
  container:{
    flex:1,
    backgroundColor:"#ddd",
    padding:"8px",
  },
  section:{
    flex:1,
    flexDirection:"row",
    justifyContent:"space-between",
  },
  sectionItemLeft:{
    flex:1,
  },
  sectionItemRight:{
    flex:3,
  },

  text:{
    fontWeight:"bold",
  },
  input:{
    padding:"5px",
  },
});
