import React, { useState } from 'react';
import { StyleSheet, Text,Image, View,ScrollView, TextInput, FlatList, TouchableOpacity,Dimensions  } from 'react-native';

import firebase from 'firebase';


const { width: screenWidth} = Dimensions.get('window')

const imageWidth = screenWidth-20;
const imageHeight = imageWidth*.8;


export default function PetDetailsScreen() {
    // const { itemId, otherParam } = route.params;
    const DetailsScreen = firebase.database().ref("DetailsScreen");
    const [details,setDetails] = React.useState({
        id:"Loading",
        type:"Loading"
    });

    const [data,setData] = React.useState("Loading")
    
    React.useEffect(() => {
        DetailsScreen.on("value",detailsSnap => {
            setDetails(detailsSnap.val());
            const Data = firebase.database().ref(detailsSnap.val().type);
            Data.on("value",data => {
                var dataVal = Object.values(data.val()).filter(function (element){
                    return element.id === detailsSnap.val().id;
                });                
                setData(dataVal[0]);
            })
        })  
    },[]);

    return(
        <View style={styles.container}>
            <Image 
                style = {styles.image}
                source={{uri:data.imageUri}}
            />
            <View style={styles.name}>
                <Text style={styles.nameTag}>{data.name}</Text>
            </View>
            <View style={{alignSelf:"flex-start"}}>

                <Text style={{fontWeight:"bold",fontSize:17}}>Cost : </Text>            <Text>{data.cost}</Text>
                <Text style={{fontWeight:"bold",fontSize:17}}>Life Span: </Text>        <Text>{data.lifespan}</Text>
                <Text style={{fontWeight:"bold",fontSize:17}}>Current Age : </Text>     <Text>{data.currentAge}</Text>
                <Text style={{fontWeight:"bold",fontSize:17}}>Location : </Text>        <Text>{data.location}</Text>
                <Text style={{fontWeight:"bold",fontSize:17}}>Disabled : </Text>        <Text>{data.handicap}</Text>
            </View>
        </View>
    );
}


const styles= StyleSheet.create({
    container:{
        padding:"10px",
        justifyContent:"center",
        alignItems:"center",
    },

    image:{
        width:imageWidth,
        height:imageHeight,
        borderRadius:"10px",
    },
    name:{
        top:-30,
        backgroundColor:"#fff",
        borderRadius:"30px",
        height:'40px',
        width:"250px",
        justifyContent:"center",
        shadowColor: '#000000',
        shadowOffset: {
            width: 0,
            height: 3
        },
        shadowRadius: 5,
        shadowOpacity: 1.0
    },
    nameTag:{
        alignSelf:"center",
        fontWeight:"bold",
        fontSize:20,
    }


});
