import React, { useState } from 'react';
import { StyleSheet, Text,Image, View,ScrollView, TextInput, FlatList, TouchableOpacity,Dimensions  } from 'react-native';

import firebase from 'firebase';


const { width: screenWidth} = Dimensions.get('window')

const imageWidth = screenWidth-"20px";
const imageHeight = imageWidth;


export default function PetDetailsScreen() {
    // const { itemId, otherParam } = route.params;
    const DetailsScreen = firebase.database().ref("DetailsScreen");
    const [details,setDetails] = React.useState({
        id:"Loading",
        type:"Loading"
    });

    const [data,setData] = React.useState("Loading")
    
    React.useEffect(() => {
        DetailsScreen.on("value",detailsSnap => {
            setDetails(detailsSnap.val());
            const Data = firebase.database().ref(detailsSnap.val().type);
            Data.on("value",data => {
                var dataVal = Object.values(data.val()).filter(function (element){
                    return element.id === detailsSnap.val().id;
                });                
                setData(dataVal[0]);
            })
        })  
    },[]);

    return(
        <View style={styles.container}>
            <Image 
                style = {styles.image}
                source={{uri:data.imageUri}}
            />
                <Text>{data.name}</Text>
        </View>
    );
}


const styles= StyleSheet.create({
    container:{
        padding:"10px",
    },
    
    image:{
        width:imageWidth,
        height:imageHeight,
        resizeMode: 'stretch',
    },
});
