import React, { useState } from 'react';
import { StyleSheet, Text,Image, View,ScrollView, TextInput, FlatList, TouchableOpacity,Dimensions  } from 'react-native';

import firebase from 'firebase';
import Icon from 'react-native-vector-icons/MaterialCommunityIcons';

const { width: screenWidth} = Dimensions.get('window')

const imageWidth = screenWidth*.25;
const imageHeight = imageWidth*.8;


export default function PetDetailsScreen() {
    // const { itemId, otherParam } = route.params;
    const CartScreen = firebase.database().ref("Cart");
 
    const [data,setData] = React.useState([])
    
    React.useEffect(() => {
       CartScreen.on("value",dataSnap => {
        setData(Object.values(dataSnap.val()));
       });
    },[]);

    // const handleCartPress = () => {
    //     const myCart = firebase.database().ref("Cart"); 
    //     myCart.push(data);
    // }

    // const handleBuyPress = () => {
    //     console.log(data.name," is added to cart");
    // }

    return(
        <View style={styles.container}>
          {
          data.map((cartItem,index) => (
            <View style={{alignItems:"space-between"}}> 

              <View  style={{flexDirection:"row"}} key={index}>
                <Image 
                  style={styles.image}
                  source={{uri:cartItem.imageUri}}
                  />
                <Text style={{fontWeight:'bold'}}>{cartItem.name}</Text>
                
                <View>
                  <Icon
                    size={30}
                    name="minus-circle-outline"  
                    />
                </View>

            </View>

            <View style={{borderBottomColor:"#000",borderBottomWidth:"2px"}}></View>
        </View>
          ))
          }


        </View>
    );
}


const styles= StyleSheet.create({
    container:{
        padding:"10px",
    },

    image:{
        borderRadius:"10px",
        width:imageWidth,
        height:imageHeight,
        margin:"2px",
    },
    shadow:{
        shadowColor: '#000000',
        shadowOffset: {
            width: 0,
            height: 3
        },
        shadowRadius: "10px",
        shadowOpacity: 1.0,
    },
    name:{
        top:-30,
        backgroundColor:"#fff",
        borderRadius:"30px",
        height:'40px',
        width:"250px",
        margin:"3px",
        justifyContent:"center",
        alignContent:"center",
    },
    nameTag:{
        alignSelf:"center",
        fontWeight:"bold",
        fontSize:20,
    }


});
